import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Typography, Grid } from '@material-ui/core';

const useStyles = makeStyles( ( theme ) => ( {
    root: {
        flexGrow: 1,
        color: 'white',
        minHeight: '80vh',
        textAlign: 'center'
    },
    menuButton: {
        marginRight: theme.spacing( 2 ),
    },
    title: {
        flexGrow: 1,
    },
    banner: {
        marginTop: '2em',
        display: 'flex',
        background: `black no-repeat center center fixed`,
        backgroundSize: 'cover',
        minHeight: '30vh',
        alignItems: 'center',
        textAlign: 'center',
        boxShadow: '3px 3px 20px black',
        borderRadius: '6px',
        textShadow: '3px 3px 20px black',
        justifyContent: 'center',
        padding: '1em',

    },
    container: {

    },
    bannerContent: {

    }
} ) );

export default function LandingPage()
{
    const classes = useStyles();

    return (
        <Grid container>

            <div className={classes.root}>
                <div className={classes.banner}>
                    <div className={classes.container}>
                        <div className={classes.bannerContent}>
                            <Typography variant="h2" gutterBottom>
                                EZBuy
                        </Typography>
                            <Typography style={{ textAlign: 'right' }} variant="body1" gutterBottom>
                                אקוסיסטם מקומי ברמת מתחמי הקניות, בהם בתי העסק משתתפים לקבלת שירות של חנות וירטואלית במודל של אמאזון. <br />
                            זה הבסיס, אבל החידוש הוא במערכת השילוח ללקוחות תוך שימוש במערכות לוקרים חכמים הממוקמים בנקודות אסטרטגיות בנקודות המוצא והיעד של המוצר.
                        </Typography>

                        </div>
                    </div>
                </div>
            </div>
        </Grid>
    );
}
