import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Typography, grid } from '@material-ui/core';

const useStyles = makeStyles( ( theme ) => ( {
    root: {
        flexGrow: 1,
        color: 'white',
        minHeight: '80vh',
        textAlign: 'center'
    },
    menuButton: {
        marginRight: theme.spacing( 2 ),
    },
    title: {
        flexGrow: 1,
    },
    banner: {
        marginTop: '2em',
        display: 'flex',
        background: `url(https://images.pexels.com/photos/34577/pexels-photo.jpg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260) no-repeat center center fixed`,
        backgroundSize: 'cover',
        minHeight: '30vh',
        alignItems: 'center',
        textAlign: 'center',
        boxShadow: '3px 3px 20px black',
        borderRadius: '6px',
        textShadow: '3px 3px 20px black',
        justifyContent: 'center'
    },
    container: {

    },
    bannerContent: {

    }
} ) );

export default function LandingPage()
{
    const classes = useStyles();

    return (
        <div className={classes.root}>
            <div className={classes.banner}>
                <div className={classes.container}>
                    <div className={classes.bannerContent}>
                        <Typography variant="h2" gutterBottom>
                            EZBuy
                        </Typography>
                        <Typography variant="h2" gutterBottom>
                            קוסיסטם מקומי ברמת מתחמי הקניות, בהם בתי העסק משתתפים לקבת שירות של חנות וירטואלית במודל של אמאזון.
                            זה הבסיס, אבל החידוש הוא במערכת השילוח ללקוחות תוך שימוש במערכות לוקרים חכמים הממוקמים בנקודות אסטרטגיות בנקודות המוצא והיעד של המוצר
                        </Typography>

                    </div>
                </div>
            </div>
        </div>
    );
}
